package ledger

import (
	"testing"
)

func TestVerifyBlockBody(t *testing.T) {
	testCases := []struct {
		name          string
		blockHexCbor  BlockHexCbor
		expectedValid bool
		expectedErr   string
	}{
		{
			// https://cardanoscan.io/block/10558501
			name: "TestVerifyBlockBody success",
			blockHexCbor: BlockHexCbor{
				Flag:          0,
				HeaderCbor:    "",
				Eta0:          "95d9b8cbdcde43708d2cf6d1b82e3e7455a6c7a1cc68096905d4d06a48a99159",
				Spk:           129600,
				BlockBodyCbor: "",
			},
			expectedValid: true,
		},
		{
			// https://cardanoscan.io/block/10558809
			// 0 tx
			name: "TestVerifyBlockBody success",
			blockHexCbor: BlockHexCbor{
				Flag:          0,
				HeaderCbor:    "",
				Eta0:          "95d9b8cbdcde43708d2cf6d1b82e3e7455a6c7a1cc68096905d4d06a48a99159",
				Spk:           129600,
				BlockBodyCbor: "80",
			},
			expectedValid: true,
		},
		{
			name: "TestVerifyBlockBody should failed",
			blockHexCbor: BlockHexCbor{
				Flag:          0,
				HeaderCbor:    "",
				Eta0:          "95d9b8cbdcde43708d2cf6d1b82e3e7455a6c7a1cc68096905d4d06a48a99159",
				Spk:           129600,
				BlockBodyCbor: "",
			},
			expectedValid: false,
		},
	}

	for _, tc := range testCases {
		t.Run(tc.name, func(t *testing.T) {
			err, isValid, _, _, _ := VerifyBlock(tc.blockHexCbor)
			if tc.expectedValid != isValid {
				t.Errorf("unexpected error: %v", err)
			}
		})
	}
}
